package marco.util;

import java.awt.geom.Point2D;

public class Vector extends Point2D.Double {

	public static final Vector ZERO = new Vector(0, 0);

	/**
	 * Construtor.
	 * 
	 * @param x um double que determina o (x,_)
	 * @param y um double que determina o (_,y)
	 */
	public Vector(double x, double y) {
		super(x, y);
	}

	/**
	 * Construtor.
	 * 
	 * @param vector vetor usado para criar um novo;
	 */
	public Vector(Vector vector) {
		super(vector.getX(), vector.getY());
	}

	/**
	 * 
	 * @return Retorna a magnetude de um vetor.
	 */
	public double length() {
		double dx = Math.pow(this.x, 2);
		double dy = Math.pow(this.y, 2);
		return Math.sqrt(dx);
	}

	/**
	 * Adiciona 2 vetores, sendo um a propria instacia e o outro passado como
	 * argumento. é possivel, passar um coeficiente que irá mutiplicar o segundo
	 * vetor.
	 * 
	 * @param vector      Vetor a ser adicionado.
	 * @param coefficient Coficiente mutiplicador do vetor passado.
	 * @return
	 * 
	 *         <img width="400px" src="
	 *         data:image/jpeg;base64,">
	 */
	public Vector add(Vector vector, double coefficient) {
		double cx = coefficient * vector.getX();
		double cy = coefficient * vector.getY();
		return new Vector(this.getX() + cx, this.getY() + cy);
	}

	/**
	 * Mutiplica o vetor por um coficiente.
	 * 
	 * @param coefficient Coeficiente para mudar a magnitude.
	 * @return <img width="400px" src="
	 *         data:image/png;base64,">
	 */
	public Vector mul(double coefficient) {
		return new Vector(coefficient * this.getX(), coefficient * this.getY());
	}

	/**
	 * 
	 * @param angle
	 * @param origin
	 * @return
	 */
	// degrees
	public Vector rotate(double angle, Vector origin) {
		double originX = origin.getX();
		double originY = origin.getY();
		double pointX = getX();
		double pointY = getY();
		double sinVar = (double) Math.sin(Math.toRadians(angle));
		double cosVar = (double) Math.cos(Math.toRadians(angle));
		double resX = originX + (pointX - originX) * cosVar - (pointY - originY) * sinVar;
		double resY = originY + (pointY - originY) * cosVar + (pointX - originX) * sinVar;
		return new Vector(resX, resY);
	}

	/**
	 * Retorna o ângulo em relação as cordenadas do robocode.
	 * <img width="400px" src="
	 * data:image/png;base64,">
	 * 
	 * @return
	 */
	public double getAngle() {
		return Util.getAngle(-this.getX(), -this.getY());
	}

	@Override
	public String toString() {
		return "Vector(" + this.getX() + ", " + this.getY() + ")";
	}

	/**
	 * Clamp as componestes do vetor, isso é as componentes do vetor passam a estar
	 * dentro de um intervalo.
	 * 
	 * @param xMin limite inferior para a componente X.
	 * @param xMax limite superior para a componente X.
	 * @param yMin limite inferior para a componente Y.
	 * @param yMax limite superior para a componente Y.
	 * @return
	 */
	public Vector bound(double xMin, double xMax, double yMin, double yMax) {
		return new Vector(Util.clamp(this.getX(), xMin, xMax), Util.clamp(this.getY(), yMin, yMax));
	}

}
